mapper.select:
SELECT post.id ,post.deleted ,post.process ,post.created_at as createdAt ,post.modified_at as modifiedAt
,post.user_id as userId ,post.title ,post.content
,user.nick as userNick FROM post, user WHERE post.user_id = user.id AND post.id = #{id}

SELECT post.id
FROM post
WHERE post.id is not NULL
AND post.deleted = #{deleted}
AND post.title LIKE CONCAT('%', #{title}, '%')


	AND #{cursor} > post.id
	ORDER BY post.id DESC


	AND post.id > #{cursor}
	ORDER BY post.id ASC

LIMIT 0, #{perpage}
